#!/usr/bin/env node
/* Simple example showing logging via TCP with callbacks */

var logger = require('../index.js')
              .set("", "user", "chris-laptop", "localhost:5140");
            
logger.on("error", function(err) {
  console.log("Received Error : " + err);
});

logger.send('I am testing an error log!', 'crit', 'yet-another-test');
logger.send('I am testing a non-error log!');
