var assert = require('assert');
var resolve = require('../');

exports.filter = function () {
    var dir = __dirname + '/resolver';
    var res = resolve.sync('./baz', {
        basedir : dir,
        packageFilter : function (pkg) {
            pkg.main = 'doom'
            return pkg;
        }
    });
    assert.equal(res, dir + '/baz/doom.js');
};
